declare module '*.svg' {
    import React from 'react';
    const ReactComponent: React.FunctionComponent<React.SVGProps<SVGSVGElement>>;
    export { ReactComponent };
    export default string;
}

declare module '*.webp' {
    const value: string;
    export default value;
}

declare module '*.jpg' {
    const value: string;
    export default value;
}